import {
  Injectable,
  ComponentFactoryResolver,
  ComponentFactory,
  Type,
  Injector,
  ApplicationRef,
  TemplateRef,
  Inject,
  Optional
} from '@angular/core';
import {
  DialogContainerComponent,
  DialogOptions,
  IDialog
} from './dialog-container/dialog-container.component';
import { IW_DIALOG_CONFIG } from './dialog.config';
import { DialogConfig } from './dialog-config.interface';
import { DialogRef } from './dialog-ref.class';
export { IDialog };

export interface IDialogService {
  open: <T>(componentType: Type<T>, options?: DialogOptions, data?: {[key: string]: any}) => IDialog;
  openTemplateRef: <T>(templateRef: TemplateRef<T>, context: T, options: DialogOptions) => IDialog;
  close: () => void;
}

@Injectable()
export class DialogService implements IDialogService {
  private __containerFactory: ComponentFactory<DialogContainerComponent>;

  private __defaultOptions: DialogOptions = {
    containerClass: '',
    clickToClose: true,
    escToClose: true,
    onClose: () => {}
  };

  private __previousDialog: DialogRef;

  constructor(
    private appRef: ApplicationRef,
    private injector: Injector,
    private componentFactoryResolver: ComponentFactoryResolver,
    @Optional() @Inject(IW_DIALOG_CONFIG) private dialogConfig: DialogConfig
  ) {
    this.__containerFactory = this.componentFactoryResolver.resolveComponentFactory(DialogContainerComponent);
  }

  /**
   * Open a dialog that embeds an entry component.
   * @param componentType the component class to embed into the dialog
   * @param options the options to use with the dialog
   * @param data the data to pass to the embedded component
   */
  open<T>(componentType: Type<T>, options?: DialogOptions, data?: {[key: string]: any}): IDialog {
    const factory = this.componentFactoryResolver.resolveComponentFactory<{[key: string]: any}>(componentType);
    const component = factory.create(this.injector);
    const _data = data || {};
    for (const key in _data) {
      if (_data.hasOwnProperty(key)) {
        component.instance[key] = _data[key];
      }
    }
    const dialogRef = new DialogRef(
      this.__containerFactory.create(this.injector, [[component.location.nativeElement]]),
      component.hostView,
      this.appRef,
      () => {
        this.__close(dialogRef);
      },
      this.__createOptions(options)
    );
    return this.__open(dialogRef);
  }

  /**
   * Open a dialog that loads a template.
   * @param templateRef the template to load inside the dialog
   * @param context the context to associate with the template
   * @param options the options to use with the dialog
   */
  openTemplateRef<T>(templateRef: TemplateRef<T>, context: T, options: DialogOptions): IDialog {
    const view = templateRef.createEmbeddedView(context);
    const dialogRef = new DialogRef(
      this.__containerFactory.create(this.injector, [view.rootNodes]),
      view,
      this.appRef,
      () => {
        this.__close(dialogRef);
      },
      this.__createOptions(options)
    );
    return this.__open(dialogRef);
  }

  /**
   * Close the currently opened dialog, if any
   */
  close() {
    if (this.__previousDialog) {
      this.__close(this.__previousDialog);
    }
  }

  private __close(dialogRef: DialogRef) {
    if (this.__previousDialog === dialogRef) {
      this.__previousDialog.detach();
      this.__previousDialog = undefined;
    }
  }

  private __open(dialogRef: DialogRef): IDialog {
    this.close();
    this.__previousDialog = dialogRef;
    dialogRef.attach();
    return dialogRef;
  }

  private __createOptions(_options?: DialogOptions) {
    const dialogConfig = this.dialogConfig || {};
    const options = _options || {};
    return Object.assign({}, this.__defaultOptions, dialogConfig, options, {
      containerClass: (dialogConfig.containerClass || '') + ' ' + (options.containerClass || '')
    });
  }
}

import {
  Injectable,
  TemplateRef,
  ComponentRef,
  Injector,
  ComponentFactoryResolver,
  ApplicationRef,
  EmbeddedViewRef,
  Type,
  Optional,
  Inject
} from '@angular/core';
import { PopoverContainerComponent } from './popover-container/popover-container.component';
import { PopoverScrollMaskComponent } from './popover-scroll-mask/popover-scroll-mask.component';
import { IW_POPOVER_CONFIG } from './popover.config';
import { PopoverConfig } from './popover-config.interface';
import { smartPosition, addClasses, combineOptionsAndDefaults } from './helpers';
import { PopoverOptions } from './popover-options.interface';
export { PopoverPosition } from './popover-position.interface';
export { PopoverOptions };

export interface IPopover {
  close: () => void;
}

class PopoverImpl implements IPopover {
  private isClosed = false;

  constructor(
    private embeddedViewRef: EmbeddedViewRef<any>,
    private appRef: ApplicationRef,
    private componentRef: ComponentRef<any>,
    private container: ComponentRef<PopoverContainerComponent>,
    private scrollMask: ComponentRef<PopoverScrollMaskComponent>
  ) {

  }

  close() {
    if (this.isClosed) {
      return;
    }
    this.isClosed = true;
    if (this.embeddedViewRef) {
      this.embeddedViewRef.destroy();
      this.appRef.detachView(this.embeddedViewRef);
    }
    if (this.componentRef) {
      this.componentRef.destroy();
      this.appRef.detachView(this.componentRef.hostView);
    }
    this.appRef.detachView(this.container.hostView);
    this.appRef.detachView(this.scrollMask.hostView);
    this.container.destroy();
    this.scrollMask.destroy();
  }
}

@Injectable()
export class Popover {
  private __instance: IPopover;

  constructor() { }

  close() {
    this.__instance.close();
  }

  setInstance(instance: IPopover) {
    this.__instance = instance;
  }
}

@Injectable()
export class PopoverService {

  constructor(
    private injector: Injector,
    private componentFactoryResolver: ComponentFactoryResolver,
    private appRef: ApplicationRef,
    @Optional() @Inject(IW_POPOVER_CONFIG) private popoverConfig: PopoverConfig
  ) { }

  openTemplateRef<T>(templateRef: TemplateRef<T>, target: HTMLElement, options: PopoverOptions): IPopover {
    return this.__open(
      null,
      templateRef.createEmbeddedView(null),
      target,
      combineOptionsAndDefaults(this.popoverConfig, options)
    );
  }

  open<T>(componentType: Type<T>, target: HTMLElement, options?: PopoverOptions, init?: (component: T) => void): IPopover {
    const reflInj = Injector.create([{provide: Popover, deps: []}], this.injector);

    const factory = this.componentFactoryResolver.resolveComponentFactory(componentType);
    const component = factory.create(reflInj);
    if (init) {
      init(component.instance);
    }
    const popover: Popover = reflInj.get(Popover);
    const popoverOptions: PopoverOptions = Object.assign({}, options || {});
    popoverOptions.shouldClose = () => {
      if (options && options.shouldClose) {
        options.shouldClose();
      }
      popover.close();
    };

    const instance = this.__open(
      component,
      null,
      target,
      combineOptionsAndDefaults(this.popoverConfig, popoverOptions)
    );
    popover.setInstance(instance);
    return instance;
  }

  private __open<T>(
    componentRef: ComponentRef<T>,
    embeddedViewRef: EmbeddedViewRef<T>,
    target: HTMLElement,
    options: PopoverOptions
  ) {
    const arrowElement = document.createElement('div');
    arrowElement.classList.add('iw-popover-arrow-element');

    // create the popover container
    const container = this.componentFactoryResolver.resolveComponentFactory(PopoverContainerComponent)
      .create(this.injector, componentRef ? [[componentRef.location.nativeElement]] : [embeddedViewRef.rootNodes]);
    // create the mask component
    const scrollMask = this.componentFactoryResolver.resolveComponentFactory(PopoverScrollMaskComponent)
      .create(this.injector, [[container.location.nativeElement, arrowElement]]);

    // we bind to the output (which is an observable)
    scrollMask.instance.clickOutsideToClose = options.clickOutsideToClose;
    scrollMask.instance.onClose.subscribe(() => {
      options.shouldClose();
    });

    container.instance.escToClose = options.escToClose;
    container.instance.onClose.subscribe(() => {
      options.shouldClose();
    });


    if (componentRef) {
      this.appRef.attachView(componentRef.hostView);
    }
    if (embeddedViewRef) {
      this.appRef.attachView(embeddedViewRef);
    }
    this.appRef.attachView(container.hostView);
    this.appRef.attachView(scrollMask.hostView);

    this.__showPopover({
      arrowElement,
      container: container.location.nativeElement,
      scrollMask: scrollMask.location.nativeElement,
      target
    }, options);

    return new PopoverImpl(
      embeddedViewRef,
      this.appRef,
      componentRef,
      container,
      scrollMask
    );
  }

  private __showPopover(elements: {
    container: HTMLElement
    scrollMask: HTMLElement
    arrowElement: HTMLElement
    target: HTMLElement
  }, options: PopoverOptions) {
    const container: HTMLElement = elements.container;
    const scrollMask: HTMLElement = elements.scrollMask;
    const arrowElement: HTMLElement = elements.arrowElement;

    setTimeout(() => {
      container.style.visibility = 'hidden';
      arrowElement.style.visibility = 'hidden';
      addClasses(container, options.popoverClass);
      addClasses(scrollMask, options.scrollMaskClass);
      addClasses(arrowElement, options.arrowClass);
      document.body.appendChild(scrollMask);

      smartPosition(elements, options);

      container.classList.add('iw-popover-container--visible');
      arrowElement.classList.add('iw-popover-arrow-element--visible');
    }, 0);
  }

}

import { Observable } from 'rxjs';
import { ButtonState } from './button-state';

export interface IStateButtonDirective {
  state: Observable<ButtonState>;
}

import { Component, OnInit } from '@angular/core';
import { PropertyEditPopoverComponent } from '../property-edit-popover/property-edit-popover.component';
import { PopoverService } from '../../../ui/popover/popover.module';

@Component({
  selector: 'app-popover-demo',
  templateUrl: './popover-demo.component.html',
  styleUrls: ['./popover-demo.component.scss']
})
export class PopoverDemoComponent implements OnInit {
  editedPerson: Person;
  editedField: string;
  persons: Person[] = [];
  firsts = ['Mark', 'Edy', 'Jack', 'John', 'Mike'];
  lasts = ['Johnson', 'Willis', 'Chapman', 'Bob', 'Last'];
  isPopoverOpen3 = false;
  isPopoverOpen2 = false;
  isPopoverOpen = false;
  isPopoverOpen4 = false;
  isPopoverOpen5 = false;

  constructor(private popoverService: PopoverService) {
    for (let i = 0; i < 1000; i++) {
      this.persons.push({
        first: this.firsts[Math.floor(Math.random() * this.firsts.length)],
        last: this.lasts[Math.floor(Math.random() * this.lasts.length)],
      });
    }
  }

  trackBy(index: number) {
    return index;
  }

  openEditFirstNamePopover(person: Person, $event: MouseEvent) {
    return this.openEditPropertyPopover(person, 'First Name', 'first', $event);
  }

  openEditLastNamePopover(person: Person, $event: MouseEvent) {
    return this.openEditPropertyPopover(person, 'Last Name', 'last', $event);
  }

  openEditPropertyPopover(person: Person, propertyName: string, p: keyof Person, $event: MouseEvent) {
    this.popoverService.open(PropertyEditPopoverComponent, <HTMLElement>$event.target, {
      popoverClass: 'popover-demo',
      escToClose: true
    }, component => {
      component.value = person[p];
      component.propertyName = propertyName;
      component.onValueChange.subscribe((v: string) => {
        person[p] = v;
      });
    });
  }

  ngOnInit() {
  }
}

interface Person {
  first: string;
  last: string;
}

export interface PopoverConfig {
  popoverClass?: string;
  arrowClass?: string;
  horizontalAlignment?: 'leftWithLeft';
  scrollMaskClass?: string;
  escToClose?: boolean;
  clickOutsideToClose?: boolean;
  horizontal?: boolean;
}

import { PopoverPosition } from './popover-position.interface';

export interface PopoverOptions {
  popoverClass?: string;
  arrowClass?: string;
  horizontal?: boolean;
  horizontalAlignment?: 'leftWithLeft' | undefined;
  scrollMaskClass?: string;
  escToClose?: boolean;
  clickOutsideToClose?: boolean;
  shouldClose?: () => void;
  popoverPosition?: (p: PopoverPosition) => void;
}

export interface TooltipConfig {
  containerClass?: string;
  horizontal?: boolean;
  delay?: number;
}

