iw-checkbox {
  display: inline-block;
  line-height: 15px;

  &:not(.checkbox--disabled) {
    cursor: pointer;
  }
  &.checkbox--disabled {
    cursor: not-allowed;
  }

  &.checkbox--disabled {
    .checkbox__checkbox {
      opacity: 0.2;
    }
  }

  &:not(.checkbox--disabled):not(.checkbox--checked):hover {
    .checkbox__checkbox {
      border-color: grey;
    }
  }

  &.checkbox--checked {
    .checkbox__checkbox {
      border-color: black;
    }

    .checkbox__checkbox svg {
      opacity: 1;
    }
  }

  .checkbox__checkbox {
    width: 15px;
    height: 15px;
